import React, { Component } from "react";

class Table extends Component {
  state = {
    list: {
      name: [],
      image: [],
      role: [],
    },
  };
  render() {
    var { list } = this.state;
    const { champions } = this.props;
    // console.log(champions);
    const name_list = Object.keys(champions).map((name) => {
      return name;
    });
    const entries = Object.entries(champions);
    const image_list = entries.map((champion) => {
      return champion[1].image.full;
    });
    const role_list = entries.map((champion) => {
      return champion[1].tags;
    });
    console.log(name_list, image_list, role_list);
    const length = name_list.length;

    var list;

    for (var i = 0; i < length; i++) {
      list.name.push(name_list[i]);
      list.image.push(image_list[i]);
      list.role.push(role_list[i]);
    }
    // console.log(list);
    this.setState({ list });
    return (
      <table className="table">
        <thead>
          <tr>
            <th>Image</th>
            <th>Champion</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {/* {list.map((champion) => (
            <tr>
              <td>{champion.image}</td>
              <td>{champion.name}</td>
              <td>{champion.role}</td>
            </tr>
          ))} */}
          {console.log(list)}
        </tbody>
      </table>
    );
  }
}

export default Table;
